const Pool = require('pg').Pool;

const pool = new Pool({
    user: "postgres",
    host: "localhost",
    //database: "music",
    database: "airplanes",
    password: "postgres",
    port:5432 
});

const getPlane = (request, response) => {
    pool.query('SELECT * FROM plane', (error, result) => {
        if(error){
            throw error;
        }
        response.status(200).json(result,row);
    });
}

module.exports = {
    getPlane: getPlane
};